
    CREATE TABLE players(
        player_id TEXT,
        name TEXT,
        height INT,
        weight INT,
        pos TEXT,
        draft_year INT,
        pick TEXT,
        birthday TEXT,
        allstars TEXT,
        allnba_first TEXT,
        allnba_second TEXT,
        allnba_third TEXT,
        alld_first TEXT,
        alld_second TEXT,
        mip TEXT,
        dpoy TEXT,
        mvp TEXT,
        finals_mvp TEXT,
        PRIMARY KEY (player_id)
    )
    